mapper.select:
SELECT c.c_sq, c.u_sq, c.img_sq, (SELECT b.file_stre_cours || '/' || b.stre_file_nm FROM comtnfiledetail b WHERE b.atch_file_id = c.img_sq AND file_cn='M0') AS imgSrc, (SELECT b.file_stre_cours || '/' || b.stre_file_nm FROM comtnfiledetail b WHERE b.atch_file_id = c.img_sq AND file_cn='M3') AS imgSrc3, c.c_dt, c.c_st, cd2.mcls_nm c_st_nm, c.c_gb, c.c_category_st, c.c_area_st, cd.mcls_nm c_area_st_nm, c.c_title, TO_CHAR(c.u_start_dt, 'yyyy-MM-dd') AS u_start_dt, TO_CHAR(c.c_end_dt, 'yyyy-MM-dd') AS c_end_dt, c.c_time, c.c_small_no, c.c_big_no, c.c_apply_no, c.c_adr, c.c_cost_no, c.c_web_adr FROM off_class c LEFT JOIN common_cd_scls cd ON (c.c_area_st = cd.mcls_cd AND cd.lcls_cd='ARA') LEFT JOIN common_cd_scls cd2 ON (c.c_st = cd2.mcls_cd AND cd.lcls_cd='CST')
AND c.c_category_st = #{classAreaSt}
AND ROWNUM <= #{imgCnt}
ORDER BY c.c_apply_no desc
ORDER BY c.u_start_dt desc

SELECT c.c_sq, c.u_sq, u.phone_no AS mblPno, u.u_nm AS usrNm, c.img_sq,
(SELECT b.file_stre_cours || '/' || b.stre_file_nm FROM comtnfiledetail b WHERE b.atch_file_id = c.img_sq AND file_cn='M0') AS imgSrc, (SELECT b.file_stre_cours || '/' || b.stre_file_nm FROM comtnfiledetail b WHERE b.atch_file_id = c.img_sq AND file_cn='D') AS imgSrc2, (SELECT b.file_stre_cours || '/' || b.stre_file_nm FROM comtnfiledetail b WHERE b.atch_file_id = c.img_sq AND file_cn='M3') AS imgSrc3,
c.c_dt, c.c_st, cd2.mcls_nm c_st_nm, c.c_gb, c.c_category_st, c.c_area_st, cd.mcls_nm c_area_st_nm, c.c_title, TO_CHAR(c.u_start_dt, 'yyyy-MM-dd') AS u_start_dt, TO_CHAR(c.c_end_dt, 'yyyy-MM-dd') AS c_end_dt, c.c_time, c.c_small_no, c.c_big_no, c.c_apply_no, c.c_adr, c.c_cost_no, c.c_web_adr FROM off_class c LEFT JOIN common_cd_scls cd ON (c.c_area_st = cd.mcls_cd AND cd.lcls_cd='ARA') LEFT JOIN common_cd_scls cd2 ON (c.c_st = cd.mcls_cd AND cd.lcls_cd='CST') LEFT JOIN users u ON (c.u_sq = u.u_sq) WHERE c.c_sq = #{classId}
