mapper.update:
	
	UPDATE USERS
	SET    U_PW  = #{usrPw}
    
    
    	
    		
    			AND U_SQ  = #{usrId}
    		
    		
    			AND u_email  = #{emailAddr}

mapper.select:
SELECT B.U_SQ usrId, B.U_PW usrPw, B.U_NM usrNm, B.PHONE_NO mblPno, B.U_EMAIL emailAddr, B.JOIN_DT joinDt, B.LOGIN_DT loginDt, B.U_OUT_DT userOutDt FROM USERS B
AND B.U_SQ = #{usrId}
AND B.U_EMAIL = #{emailAddr}
